sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	// implement resource here
	var incoming = request.body.data;

	var flowData = {
		url : incoming['access_url'],
		status : incoming['status'],
		ritm : incoming['ritm'],
		is_test : incoming['is_test']
	};

	try {
		var inputs = {};
		inputs['flowdata'] = flowData; // Object 
		var result = sn_fd.FlowAPI.getRunner().subflow('x_apig_termination.onedrive_delegation_return_data').inForeground().withInputs(inputs).run();
		var outputs = result.getOutputs();	
	} catch (ex) {
		var message = ex.getMessage();
		gs.error(message);
	}
})(request, response);
