//Leave this alone - recursively check sys_ids for companies in var companies and add to the related list in var companyIDs
for (var i = 0; i < Object.keys(companies).length; i++) {
    var company = companies[Object.keys(companies)[i]];
    for (var j = 0; j < company.length; j++) {
        var companyGr = new GlideRecord('core_company');
        companyGr.addQuery('name', company[j]);
        companyGr.query();
        if (companyGr.hasNext()) {
            companyGr.next();
            companyIDs[Object.keys(companies)[i]].push(companyGr.sys_id);
        }
    }
}

