//Set company var to company from submitted form
var company = current.variables.company.sys_id;

//Get groups
var groups = getGroups(company);

//Send a notification for each group
groups.forEach(fireEvent);

function getGroups(targetCompany) {
  //init group list
  var groupList = [];

  //get groups for company
  var gr = new GlideRecord('u_company_and_group_reference');
  gr.addQuery('u_company', targetCompany);
  gr.addQuery('u_termination', true);

  gr.query();

  //add company groups to group list
  while (gr.next()) {
    if (!groupList.includes(gr.u_group.sys_id)) {
    groupList.push(gr.u_group.sys_id);
	}
  }

  //get groups that receive all notifications
  var grAll = new GlideRecord('u_company_and_group_reference');
  grAll.addQuery('u_termination', true);
  grAll.addQuery('u_termination_receive_all', true);

  grAll.query();

  //add company groups to group list
  while (grAll.next()) {
    if (!groupList.includes(grAll.u_group.sys_id)) {
    groupList.push(grAll.u_group.sys_id);
	}
  }

  return groupList;
}

function fireEvent(item, index) {
  gs.eventQueue("generic.request.notification", current, item, '');
}

