// Set 'answer' to the number of seconds this timer should wait
var ritmGr = new GlideRecord('sc_req_item');
ritmGr.get(current.sys_id);

//Get date/time difference
var startDateTime = new GlideDateTime();
var endDateTime = new GlideDateTime(ritmGr.variables.termination_date_time.getValue());

//Get difference
var diffDateTime = new GlideDateTime(GlideDateTime.subtract(startDateTime, endDateTime));

//Get difference in seconds
var diff = diffDateTime.getNumericValue()/1000;

//If the number's not negative
if(diff>0){
answer = diff;
}
else{
answer = 0;
}

